:root {
  color-scheme: light;
  --background: #fff;
  --surface: #fff;
  --surface-light: #fff;
  --border: #eee;
  --text-primary: #000;
  --text-secondary: #222;
  --accent: #e11d48;
  font-family: system-ui, -apple-system, BlinkMacSystemFont, "Segoe UI", sans-serif;
}

* {
  box-sizing: border-box;
}

body {
  margin: 0;
  background: var(--background);
  color: var(--text-primary);
  min-height: 100vh;
}

a {
  color: inherit;
  text-decoration: none;
}

a:hover,
a:focus {
  text-decoration: underline;
}

.app-container {
  display: flex;
  flex-direction: column;
  min-height: 100vh;
}


.header {
  position: sticky;
  top: 0;
  z-index: 10;
  background: #fff;
  border-bottom: 4px solid #e11d48;
  box-shadow: none;
}


.header nav {
  display: flex;
  align-items: center;
  gap: 1rem;
  padding: 1rem 2rem;
}


.header a {
  padding: 0.5rem 0.75rem;
  font-weight: 500;
  color: var(--text-secondary);
  background: none;
  border: none;
  border-radius: 0;
  transition: color 0.2s ease;
}


.header a.active {
  color: var(--accent);
  border-bottom: 2px solid var(--accent);
  background: none;
}


.header a:hover {
  color: var(--accent);
}


.content-wrapper {
  display: grid;
  grid-template-columns: minmax(220px, 280px) 1fr;
  flex: 1;
  max-width: 1200px;
  width: 100%;
  margin: 0 auto;
  padding: 2rem;
  gap: 2rem;
}


.sidebar {
  position: sticky;
  top: 5.5rem;
  align-self: flex-start;
  padding: 0;
  border-radius: 0;
  background: none;
  border: none;
  display: flex;
  flex-direction: column;
  gap: 0.75rem;
}


.sidebar h2 {
  margin: 0 0 0.75rem;
  font-size: 0.95rem;
  text-transform: uppercase;
  letter-spacing: 0.08em;
  color: var(--text-secondary);
}


.sidebar a {
  padding: 0.45rem 0.65rem;
  color: var(--text-secondary);
  font-size: 0.95rem;
  background: none;
  border: none;
  border-radius: 0;
  transition: color 0.2s ease;
}


.sidebar a.active {
  color: var(--accent);
  border-bottom: 2px solid var(--accent);
  background: none;
}


main {
  padding: 2rem;
  border-radius: 0;
  background: #fff;
  border: none;
  box-shadow: none;
}


article {
  max-width: 700px;
  margin: 0 auto;
  line-height: 1.7;
  color: var(--text-secondary);
  background: none;
  border-radius: 0;
}


article h1,
article h2,
article h3 {
  color: var(--text-primary);
}


article code {
  background-color: #f5f5f5;
  border-radius: 0;
  padding: 0.2rem 0.4rem;
  font-family: 'JetBrains Mono', 'Fira Code', monospace;
}


@media (max-width: 900px) {
  .content-wrapper {
    grid-template-columns: 1fr;
    padding: 1.5rem;
  }

  .sidebar {
    position: relative;
    top: auto;
    flex-direction: row;
    flex-wrap: wrap;
    padding: 0;
    border-radius: 0;
    background: none;
    border: none;
  }

  main {
    padding: 1.5rem;
    border-radius: 0;
    background: #fff;
    border: none;
    box-shadow: none;
  }
}

